package Service;import Model.Event;import Model.Organizer;import Model.Venue;import java.io.IOException;import java.io.PrintWriter;import java.util.ArrayList;import java.util.List;import java.util.stream.Collectors;public class EventManagement implements EventOperations {    private ArrayList<Event> events;    public EventManagement(ArrayList<Event> events) {        this.events = events;    }    @Override    public void createEvent(Event event) {        events.add(event);    }    @Override    public void updateEvent(int idToUpdate, Event updatedEvent) {        for (int i = 0; i < events.size(); i++) {            if (events.get(i).getEventId() == idToUpdate) {                events.set(i, updatedEvent);                break;            }        }    }    @Override    public boolean deleteEvent(int eventId) {        return events.removeIf(event -> event.getEventId() == eventId);    }    @Override    public List<Event> findEventsByName(String name) {        return events.stream()                .filter(event -> event.getEventName().equals(name))                .collect(Collectors.toList());    }    public Event findEventById(int id) {        return events.stream()                .filter(event -> event.getEventId() == id)                .findFirst()                .orElse(null);    }    @Override    public List<Event> listAllEvents() {        return events;    }    @Override    public void saveToFile(String fileName) {        try (PrintWriter writer = new PrintWriter(fileName)) {            for (Event event : events) {                writer.println(event.getEventId() + "," +                        event.getEventName() + "," +                        event.getOrganizerId() + "," +                        event.getVenueId() + "," +                        event.getStartDate() + "," +                        event.getEndDate() + "," +                        event.getExpectedAttendees());            }            System.out.println("✅ Events saved to " + fileName);        } catch (IOException e) {            System.out.println("❌ Error saving to file: " + e.getMessage());        }    }}